sources.style/exercise.scss:
$lineCount: 10;

.frame {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 400px;
  height: 400px;
  margin-top: -200px;
  margin-left: -200px;
  border-radius: 4px;
    box-shadow: 4px 8px 16px 0 rgba(0,0,0,0.1);
    overflow: hidden;
  background: #607D8B;
}

.center {
    position: absolute;
    width: 40px;
    height: 40px;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
}

.part {
    width: 30px;
    height: 30px;
    background-color: white;
    position: absolute;
    top: calc(50% - 15px);
    left: calc(50% - 15px);
    transition: transform 300ms cubic-bezier(0.77, 0, 0.175, 1), border-radius 100ms ease-in-out;
}

